/*eslint semi:[2,"never"]*/
'use strict'
const recast = require('recast')
const esprima = require('esprima-fb')
const escodegen = require('escodegen')
//var code = "import _ from 'a'"

const code = `import React from 'react/addons';
import _ from 'lodash';


export default function(){ return React.createElement('div',{})}`


/*eslint no-unused-vars:0*/
function recast1() {
    const tree = recast.parse(code)
    const s = recast.print(tree).code //escodegen.generate(tree, {comment: true})
    console.log(s)
}

function escodegenTest() {
    //var tree = esprima.parse(code)
    //var tree = esprima.parse(code, {sourceType: 'module'})
    const tree = esprima.parse(code, {attachComment: true, loc: false, range: false, sourceType: 'module', tokens: true})
    const s = escodegen.generate(tree)
    //var s = escodegen.generate(tree, {comment: true})
    console.log(s)
}

//recast1()
escodegenTest()


